function getFishKeys() {
    let count = 0;

    // Iterate over all keys in local storage
    for (let i = 0; i < localStorage.length; i++) {
        // Check if the key starts with "fish-"
        if (localStorage.key(i).startsWith('bugs-')) {
            count++;
        }
    }

    // Output the count in the format "Fish Progress: # / 40"
    Outputs.CountText = `Bugs Progress: ${count} / 40`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishKeys, 100);

// Define the audio file path
const audioFilePath = Inputs.AudioFilePath || '/audio/press.mp3';

// Create a new Audio object
const audio = new Audio(audioFilePath);

// Play the audio file when the script runs
audio.play().then(() => {
    Outputs.Success();
}).catch((error) => {
    console.error("Error playing audio:", error);
    Outputs.Failure();
});
