// Default icon URL
const defaultIcon = 'https://acgc.sterner.digital/images/question_mark.png';

// Get all localStorage keys that start with 'v-' and sort them numerically
function getSortedVKeys() {
    const keys = [];
    for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && key.startsWith('v-')) {
            keys.push(key);
        }
    }

    // Sort keys numerically based on their suffix
    keys.sort((a, b) => {
        const numA = parseInt(a.substring(2), 10); // Extract numerical part
        const numB = parseInt(b.substring(2), 10); // Extract numerical part
        return numA - numB;
    });

    return keys;
}

// Function to process and output icon strings
function processIcons() {
    try {
        // Retrieve and sort 'v-' keys from localStorage
        const sortedKeys = getSortedVKeys();

        // Map through the sorted keys and extract icon values
        const iconStrings = sortedKeys.slice(0, 15).map(key => {
            const storedData = localStorage.getItem(key);
            if (storedData) {
                try {
                    const parsedData = JSON.parse(storedData);
                    return parsedData.icon || defaultIcon;
                } catch (error) {
                    console.error(`Error parsing JSON for key ${key}:`, error);
                    return defaultIcon;
                }
            } else {
                return defaultIcon;
            }
        });

        // Output the icon strings to separate outputs explicitly
        Outputs.IconStrings1 = iconStrings[0] || defaultIcon;
        Outputs.IconStrings2 = iconStrings[1] || defaultIcon;
        Outputs.IconStrings3 = iconStrings[2] || defaultIcon;
        Outputs.IconStrings4 = iconStrings[3] || defaultIcon;
        Outputs.IconStrings5 = iconStrings[4] || defaultIcon;
        Outputs.IconStrings6 = iconStrings[5] || defaultIcon;
        Outputs.IconStrings7 = iconStrings[6] || defaultIcon;
        Outputs.IconStrings8 = iconStrings[7] || defaultIcon;
        Outputs.IconStrings9 = iconStrings[8] || defaultIcon;
        Outputs.IconStrings10 = iconStrings[9] || defaultIcon;
        Outputs.IconStrings11 = iconStrings[10] || defaultIcon;
        Outputs.IconStrings12 = iconStrings[11] || defaultIcon;
        Outputs.IconStrings13 = iconStrings[12] || defaultIcon;
        Outputs.IconStrings14 = iconStrings[13] || defaultIcon;
        Outputs.IconStrings15 = iconStrings[14] || defaultIcon;

        // Trigger the refresh output with a unique value
        Outputs.RefreshNode = new Date().getTime();

        // Debug log output
        Outputs.DebugLog = "Function executed at " + new Date().toISOString();

        // Signal success
        Outputs.Success();
    } catch (error) {
        // Handle error and set default outputs
        Outputs.error = error.message;
        Outputs.IconStrings1 = defaultIcon;
        Outputs.IconStrings2 = defaultIcon;
        Outputs.IconStrings3 = defaultIcon;
        Outputs.IconStrings4 = defaultIcon;
        Outputs.IconStrings5 = defaultIcon;
        Outputs.IconStrings6 = defaultIcon;
        Outputs.IconStrings7 = defaultIcon;
        Outputs.IconStrings8 = defaultIcon;
        Outputs.IconStrings9 = defaultIcon;
        Outputs.IconStrings10 = defaultIcon;
        Outputs.IconStrings11 = defaultIcon;
        Outputs.IconStrings12 = defaultIcon;
        Outputs.IconStrings13 = defaultIcon;
        Outputs.IconStrings14 = defaultIcon;
        Outputs.IconStrings15 = defaultIcon;

        // Signal failure
        Outputs.Failure();
    }
}

// Event listener for localStorage changes from other windows or tabs
window.addEventListener('storage', function(event) {
    if (event.storageArea === localStorage) {
        processIcons();
    }
});

// Check for input trigger to run processIcons
if (Inputs.Trigger) {
    processIcons();
}

// Call the function initially to populate outputs
processIcons();
