// Define the default value for the number of repetitions
const repetitions = Inputs.Repetitions || 5;

// Define the default value for the delay between repetitions
const delay = Inputs.Delay || 1000;

// Define the default value for the signal to be repeated
const signal = Inputs.Signal || 'Signal';

// Check if the page is loaded
if (Inputs.PageLoaded) {
  // Repeat the signal for the specified number of repetitions
  for (let i = 0; i < repetitions; i++) {
    // Use setTimeout to delay the signal
    setTimeout(() => {
      // Trigger the signal
      Outputs[signal]();
    }, i * delay);
  }
  // Send the Success signal
  Outputs.Success();
} else {
  // If the page is not loaded, send the Failure signal
  Outputs.Failure();
}

function getFishKeys() {
    let count = 0;

    // Iterate over all keys in local storage
    for (let i = 0; i < localStorage.length; i++) {
        // Check if the key starts with "fish-"
        if (localStorage.key(i).startsWith('fish-')) {
            count++;
        }
    }

    // Output the count in the format "Fish Progress: # / 40"
    Outputs.CountText = `Progress: ${count} / 40`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishKeys, 100);

// Load the audio file
const audioFile = Inputs.AudioFile || '/audio/loading.mp3';

// Create a new Audio object
const audio = new Audio(audioFile);

// Delay the audio playback by 5000ms (5 seconds)
setTimeout(() => {
    // Play the audio file
    audio.play().then(() => {
      // If the audio file is successfully played, send the Success signal
      Outputs.Success();
    }).catch((error) => {
      // If there is an error, send the error to the Error output and send the Failure signal
      Outputs.Error = error;
      Outputs.Failure();
    });
}, 2000);

// Define the audio file path
const audioFilePath = Inputs.AudioFilePath || '/audio/press.mp3';

// Create a new Audio object
const audio = new Audio(audioFilePath);

// Play the audio file when the script runs
audio.play().then(() => {
    Outputs.Success();
}).catch((error) => {
    console.error("Error playing audio:", error);
    Outputs.Failure();
});

// Wait for 1 second
setTimeout(() => {
    // Create new audio object
    const audio = new Audio('/audio/splash.mp3');
    // Play the audio
    audio.play();
    // Signal success
    Outputs.Success();
}, 1000);

// Wait for 0.5 seconds
setTimeout(() => {
    // Signal to navigate away
    Outputs.NavigateAway();
}, 2000);
