function checkLocalStorage() {
    // Check if the key 'zodiacSign' exists in the local storage
    if(localStorage.getItem('zodiacSign')) {
        // If it exists, get the value
        const zodiacSignValue = localStorage.getItem('zodiacSign');

        // Assign the value to the output 'ZodiacSign'
        Outputs.ZodiacSign = zodiacSignValue;

        // Extract the zodiac sign name from the URL path
        const zodiacSignMatch = zodiacSignValue.match(/\/(\w+)\.png$/);
        if (zodiacSignMatch && zodiacSignMatch[1]) {
            // Define the list of zodiac signs
            const zodiacSigns = [
                'aries', 'taurus', 'gemini', 'cancer', 
                'leo', 'virgo', 'libra', 'scorpio', 
                'sagittarius', 'capricorn', 'aquarius', 'pisces'
            ];

            const zodiacSign = zodiacSignMatch[1].toLowerCase();

            // Check if the extracted sign is a valid zodiac sign
            if (zodiacSigns.includes(zodiacSign)) {
                // Capitalize the first letter of the zodiac sign name
                Outputs.ZodiacSignText = zodiacSign.charAt(0).toUpperCase() + zodiacSign.slice(1);
            } else {
                // If the zodiac sign name is not valid, set a default value
                Outputs.ZodiacSignText = 'StarSign';
            }
        } else {
            // If the zodiac sign name is not found in the value, set a default value
            Outputs.ZodiacSignText = 'Invalid Sign';
        }

        Outputs.Success();
    } else {
        // If 'zodiacSign' doesn't exist, throw an error and call the 'Failure' signal
        Outputs.error = 'No zodiac sign found in local storage';
        Outputs.ZodiacSignText = 'Invalid Sign';
        Outputs.Failure();
    }
}

