parameters.functionScript:
function _split(name) {
  const bySpace = name.split(" ");
  const byDot = name.split(".");
  return bySpace.length > byDot.length ? bySpace : byDot;
}

function _makeBadge(first, second) {
  if (first === undefined) return undefined;
  else if (second === undefined) return (first + first).toUpperCase();
  else return (first + second).toUpperCase();
}

// ---

const name = Inputs.Name;

let badge = undefined;
try {
  const chars = _split(name);
  if (chars.length === 1) {
    badge = _makeBadge(chars[0][0], chars[0][1]);
  } else if (chars.length >= 2) {
    badge = _makeBadge(chars[0][0], chars[1][0]);
  }
} catch (e) { }

Outputs.Abbreviation = badge;

const itemSpacing = Inputs.ItemSpacing;
const items = Inputs.Items;
const maxItems = Inputs.MaxItems;

Outputs.Remapped = items.slice(0, maxItems).map((x) => ({
    ...x,
    ItemSpacing: itemSpacing,    
}));

Outputs.OverflowCount = Math.max(items.length - maxItems, 0);

