Node.Inputs = {
    Url: "string",
}

Node.Outputs = {}

let audio;

Node.Signals = {
    Play() {
        if (audio) {
            audio.pause();
        }

        audio = new Audio(Node.Inputs.Url);
        audio.playbackRate = 2; // 1 + Math.random() * 3;
        audio.loop = true;
        audio.play();
    },
    Stop() {
        if (audio) {
            audio.pause();
        }
    }
}

