// Define the default value for the number of repetitions
const repetitions = Inputs.Repetitions || 5;

// Define the default value for the delay between repetitions
const delay = Inputs.Delay || 1000;

// Define the default value for the signal to be repeated
const signal = Inputs.Signal || 'Signal';

// Check if the page is loaded
if (Inputs.PageLoaded) {
  // Repeat the signal for the specified number of repetitions
  for (let i = 0; i < repetitions; i++) {
    // Use setTimeout to delay the signal
    setTimeout(() => {
      // Trigger the signal
      Outputs[signal]();
    }, i * delay);
  }
  // Send the Success signal
  Outputs.Success();
} else {
  // If the page is not loaded, send the Failure signal
  Outputs.Failure();
}

function getFishKeys() {
    let count = 0;

    // Iterate over all keys in local storage
    for (let i = 0; i < localStorage.length; i++) {
        // Check if the key starts with "fish-"
        if (localStorage.key(i).startsWith('painting-')) {
            count++;
        }
    }

    // Output the count in the format "Fish Progress: # / 40"
    Outputs.CountText = `Paintings Progress: ${count} / 15`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishKeys, 100);

// Load the audio file
const audioFile = Inputs.AudioFile || '/audio/loading.mp3';

// Create a new Audio object
const audio = new Audio(audioFile);

// Delay the audio playback by 5000ms (5 seconds)
setTimeout(() => {
    // Play the audio file
    audio.play().then(() => {
      // If the audio file is successfully played, send the Success signal
      Outputs.Success();
    }).catch((error) => {
      // If there is an error, send the error to the Error output and send the Failure signal
      Outputs.Error = error;
      Outputs.Failure();
    });
}, 2000);

// Wait for 1 second
setTimeout(() => {
    // Create new audio object
    const audio = new Audio('/audio/splash.mp3');
    // Play the audio
    audio.play();
    // Signal success
    Outputs.Success();
}, 1000);

// Wait for 0.5 seconds
setTimeout(() => {
    // Signal to navigate away
    Outputs.NavigateAway();
}, 2000);

const id = Inputs.ID || '';
const label = Inputs.Label || '';
const checked = Inputs.Checked || false;
const icon = Inputs.Icon || ''; // Added icon
const obtained = Inputs.Obtained || ''; // Added obtained
const price = Inputs.Price || ''; // Added price

// Function to load data from localStorage
function loadSelection(id, callback) {
    const storedData = localStorage.getItem(id);
    if (storedData) {
        callback(JSON.parse(storedData));
    } else {
        callback(null);
    }
}

// Function to handle passthrough
function passthrough() {
    Outputs.ID = id;
    Outputs.Label = label;
    Outputs.Checked = checked;
    Outputs.Icon = icon; // Added icon
    Outputs.Obtained = obtained; // Added obtained
    Outputs.Price = price; // Added price
    Outputs.Failure();
}

// Main logic
loadSelection(id, function(result) {
    if (result) {
        Outputs.ID = result.id;
        Outputs.Label = result.label;
        Outputs.Checked = result.checked;
        Outputs.Icon = result.icon || ''; // Added icon handling
        Outputs.Obtained = result.obtained || ''; // Added obtained handling
        Outputs.Price = result.price || ''; // Added price handling
        Outputs.Success();
    } else {
        passthrough();
    }
});

const id = Inputs.ID || '';
const label = Inputs.Label || '';
const checked = Inputs.Checked; // Expecting a boolean input
const changed = Inputs.Changed; // Signal input, used as a trigger
const icon = Inputs.Icon || ''; // Added icon
const obtained = Inputs.Obtained || ''; // Added obtained
const price = Inputs.Price || ''; // Added price

// Function to load data from localStorage
function loadSelection(id, callback) {
    const storedData = localStorage.getItem(id);
    if (storedData) {
        callback(JSON.parse(storedData));
    } else {
        callback(null);
    }
}

// Function to save data to localStorage
function saveSelection(selection) {
    localStorage.setItem(selection.id, JSON.stringify(selection));
}

// Function to delete data from localStorage
function deleteSelection(id) {
    localStorage.removeItem(id);
}

// Function to check if data has changed and save or delete if necessary
function checkAndUpdateSelection(selection) {
    loadSelection(selection.id, function(existingItem) {
        let dataChanged = false;

        if (existingItem) {
            if (selection.checked === false) {
                // If the checkbox is unchecked, delete the entry
                deleteSelection(selection.id);
                Outputs.SavedSelection = null; // Indicate that nothing was saved
                Outputs.Success();
            } else {
                // Check if the existing data is different from the new data
                if (existingItem.label !== selection.label ||
                    existingItem.checked !== selection.checked ||
                    existingItem.icon !== selection.icon ||
                    existingItem.obtained !== selection.obtained ||
                    existingItem.price !== selection.price) {
                    dataChanged = true;
                }

                if (dataChanged) {
                    saveSelection(selection);
                    Outputs.SavedSelection = selection;
                    Outputs.Success();
                } else {
                    passthrough();
                }
            }
        } else {
            if (selection.checked) {
                // If the checkbox is checked and no existing item, save the new item
                saveSelection(selection);
                Outputs.SavedSelection = selection;
                Outputs.Success();
            } else {
                passthrough();
            }
        }
        // Signal completion
        Outputs.Completed = true; // or you can use Outputs.Completed = new Date().getTime(); for a timestamp
    });
}

// Function to handle passthrough
function passthrough() {
    Outputs.ID = id;
    Outputs.Label = label;
    Outputs.Checked = checked;
    Outputs.Icon = icon; // Added icon
    Outputs.Obtained = obtained; // Added obtained
    Outputs.Price = price; // Added price
    Outputs.Failure();
    // Signal completion
    Outputs.Completed = true; // or you can use Outputs.Completed = new Date().getTime(); for a timestamp
}

// Main logic
const selection = { id, label, checked, icon, obtained, price }; // Added obtained and price
if (changed) {
    checkAndUpdateSelection(selection);
} else {
    passthrough();
}
